export interface Eep {
    number:         string;
    title:          string;
    status?:        Status | StatusEnum;
    case?:          Case[];
    originalIndex?: number;
    eep:            string;
    rorg_title:     RorgTitle;
    rorg_number:    RorgNumber;
    func_title:     string;
    func_number:    number | string;
    description:    string;
    submitter:      string[];
    ref?:           string;
    $t?:            string;
    icon?:          string;
}

export interface Case {
    datafield:    DatafieldElement[] | DatafieldElement;
    direction?:   string;
    condition?:   Condition;
    title?:       string;
    description?: Status | string;
    status?:      StatusEnum;
    statusfield?: StatusField[];
}

export interface Condition {
    direction?:   string;
    datafield?:   Field[] | Field;
    statusfield?: StatusField[];
}

export interface Field {
    bitoffs: number | string;
    bitsize: number | string;
    value:   number | string;
}

export interface StatusField extends Field {
    data:   Data;
}

export enum Data {
    Nu = "NU",
    T21 = "T21",
}


export interface DatafieldElement {
    reserved?:    Status | string;
    data?:        Status | string;
    shortcut?:    Status | string;
    description?: Status | string;
    bitoffs?:     number | string;
    bitsize?:     number | string;
    scale?:       Scale | string;
    enum?:        EnumElement[] | EnumElement;
    info?:        Status | string;
    range?:       Scale;
    unit?:        UnitClass | string;
    spread?:      Spread[];
}

export interface Status {
}

export interface EnumElement {
    item: ItemElement[] | ItemElement;
}

export interface ItemElement {
    value?:       number | string;
    description?: string;
    min?:         string;
    max?:         string;
    scale?:       Scale | string;
    unit?:        string;
    range?:       Scale;
    info?:        Status;
    bitmask?:     string;
}


export interface Scale {
    min?: number | string;
    max?: number | string;
    ref?: string;
}

export interface Spread {
    bitoffs: number | string;
    bitsize: number | string;
}

export interface UnitClass {
    ref?: string;
}

export enum StatusEnum {
    Released = "released",
}

export enum RorgNumber {
    A5 = "0xA5",
    D2 = "0xD2",
    D5 = "0xD5",
    F6 = "0xF6",
    f6 = "0xf6",
}

export enum RorgTitle {
    RPSTelegram = "RPS Telegram",
    The1BSTelegram = "1BS Telegram",
    The4BSTelegram = "4BS Telegram",
    VLDTelegram = "VLD Telegram",
}

export const EEP : Record<string, Eep>;
