import {ByteArray, ByteArrayConstructor} from "@enocean-js/byte-array";

declare module '@enocean-js/esp3-packet' {
    export class ESP3Packet {
        constructor(...input: ByteArrayConstructor[]);

        push(...value);

        shift();

        slice(...args);

        findIndex(func);

        get length(): number;

        get dataLength(): number;

        get optionalLength(): number;

        get packetType(): number;

        set packetType(value: number) ;

        get header(): ByteArray;

        get data(): ByteArray;

        set data(value) ;

        get optionalData(): ByteArray;

        set optionalData(value) ;

        get body(): ByteArray;

        get crc8Data(): number;

        get crc8Header(): number;

        isHeaderOK(): boolean;

        isBodyOK(): boolean;

        isPacketOK(): boolean;

        fixPacket();

        toString (radix?: 'bin'| 'dec'| 'hex'|2|10|16) : string;

        toJSON(): any;

        static from(input) ;

        static fieldExtractor(res, field) ;
    }
}

